package project4b;

import java.io.Serializable;

/**
 * @author jsmith
 *
 */
public class ScienceBook implements Serializable, SpecificGenreBookInterface {

	/**
	 * Generated by Eclipse
	 */
	private static final long serialVersionUID = -4253804589971365970L;
	
	private static final String genre = "Science";
	private String attribute;
	
	private Book book;
	
	public ScienceBook(String author, String title, String isbn, String topic) {
		book = new Book(author, title, isbn);
		attribute = topic;
	}
	
	public ScienceBook() {
		book = new Book();
	}
	
	@Override
	public String getAuthor() { return book.getAuthor(); }
	
	@Override
	public String getTitle() { return book.getTitle(); }
	
	@Override
	public String getISBN() { return book.getISBN(); }

	@Override
	public String getGenre() {return genre;}

	@Override
	public String getAttribute() {return attribute;}
	
	/**
	 * Returns colon delimted string representation of Book data.
	 */
	@Override
	public String toString() {
		return(book.toString()+":"
			  +getGenre()+":"
			  +getAttribute());
	}

	/* (non-Javadoc)
	 * @see project4b.BookInterface#setAuthor(java.lang.String)
	 */
	@Override
	public void setAuthor(String author) {book.setAuthor(author);}

	/* (non-Javadoc)
	 * @see project4b.BookInterface#setTitle(java.lang.String)
	 */
	@Override
	public void setTitle(String title) {book.setTitle(title);}

	/* (non-Javadoc)
	 * @see project4b.BookInterface#setISBN(java.lang.String)
	 */
	@Override
	public void setISBN(String isbn) {book.setISBN(isbn);}
	
	@Override
	public void setAttribute(String topic) { attribute = topic; }
}
